  text-align: center;
  margin-top: 5%;
  h1 {
    color: #f2a22c;
    font-family: "Caesar Dressing", cursive;
    font-size: 50px;
    @media screen and (min-width: $break-medium) {
      font-size: 60px;
    }
    @media screen and (min-width: $break-large) {
      font-size: 90px;
    }
  }
}

