  display: flex;
  justify-content: center;
  margin-top: 10%;
  @media screen and (min-width: $break-large) {
    margin-top: 15%;
  }

  form {
    text-align: center;
    display: flex;
    flex-direction: column;
    @media screen and (min-width: $break-large) {
      flex-direction: row;
    }
    input:first-child {
      width: 80vw;
      height: 15vw;
      border: none;
      border-bottom: 4px solid #f2a22c;
      background-color: transparent;
      font-size: 20px;
      padding-left: 15px;
      margin-bottom: 10%;
