        width: 25vw;
        height: 3vw;
        margin-right: 20px;
      }

      &::placeholder {
        color: gray;
        font-weight: normal;
        font-size: 20px;
        text-align: center;
        @media screen and (min-width: $break-large) {
          text-align: left;
          font-size: 20px;
          padding-left: 0px;
        }
      }
    }
    input:nth-child(2) {
      width: 50vw;
      font-family: "Sen";
      font-weight: bold;
      border: 2px solid #f2a22c;
      border-radius: 5px;
      padding: 15px 20px;
      font-size: 15px;
      background-color: transparent;
      cursor: pointer;
      transition: 0.1s;
      text-transform: uppercase;
      margin: 0 auto;
      @media screen and (min-width: $break-medium) {
        width: 70%;
